INSERT INTO `studentinfo` VALUES (646, '电科1601', '1.059', '忠实诚信，讲原则，说到做到，绝不推卸责任，做事情坚持有始有终，会不断尝试不同的领域的学习、工作方式，决定后，会以全部的热情和精力投入到学习、工作中性格平易近人、随和，为人诚恳，有较强的团队精神，态度认真，能够熟练操作办公系统软件，对新事物接受能力快，', '无');
